DROP TABLE IF EXISTS `dt_appeal`;
CREATE TABLE `dt_appeal`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '求助表：个人；公益',
  `apply_id` int(11) DEFAULT NULL COMMENT '申请ID',
  `user_id` int(11) DEFAULT NULL COMMENT '审批人Id',
  `user_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '审批人名字',
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '求职标题',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '求助内容',
  `appeal_type` int(4) DEFAULT NULL COMMENT '求助类型：0，公益；1个人',
  `appeal_state` int(4) DEFAULT NULL COMMENT '状态：0，完成；1，进行中；',
  `appeal_verify` int(4) DEFAULT NULL COMMENT '通过：0，审核中；1，审核通过；2审核未通过',
  `appeal_count` decimal(10, 0) DEFAULT NULL COMMENT '捐赠次数',
  `file` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '封面',
  `goal` decimal(10, 2) DEFAULT NULL COMMENT '目标金额',
  `completed` decimal(10, 2) DEFAULT NULL COMMENT '完成金额',
  `gmt_created` datetime(0) DEFAULT NULL COMMENT '创建时间',
  `gmt_modified` datetime(0) DEFAULT NULL COMMENT '修改时间',
  `is_deleted` int(4) DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 28 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

