INSERT INTO `dt_appeal` VALUES (27, 28, NULL, NULL, '0531测试', '<p>今天上午，陕西门业协会监事长、西安铭耐达公司总经理、长安政协委员肖兴时，率陕西门业协会诸位爱心人士为豆角镇村贫困大学生送来一万元助学金和学习用品及过冬用的衣物。</p><p><img src=\"http://donate-store.oss-cn-beijing.aliyuncs.com/user_img/1559273524174.jpeg\"></p><p>子午街办南豆角村学生肖彩去年考入西安市职业技术学院，因其家庭贫困一度止步于校门之外，肖兴石得知后当即捐赠7000元让其圆梦大学。</p><p><br></p><p><br></p><p><img src=\"http://donate-store.oss-cn-beijing.aliyuncs.com/user_img/1559273541212.jpeg\"></p><p>今年是第二年，由于肖彩父母没有任何经济来源，学费又成了头疼的事。肖兴石今年不仅将助学金增至一万元，还送了学习用品及服装，并鼓励她好好学习，承诺今后会帮扶到她毕业后为她找到工作为止。肖彩在感激肖总的捐赠其助学金的同时，表示一定要用好的学习成绩回报帮助她的人，并会积极回报社会。</p><p><br></p><p><br></p><p><br></p><p>子午街办领导和村主任对肖兴时及陕西门业协会爱心人士义举表示感谢，同时呼吁社会关注弱势群体，让贫困学生不因学费止步于大学校门，让弱势群体感受社会的温暖。</p><p><br></p>', 1, 0, 1, 0, 'http://donate-store.oss-cn-beijing.aliyuncs.com/user_img/1559273564339.jpeg', 66666.00, 0.00, '2019-05-31 11:32:43', '2019-05-31 11:32:43', 0);

-- ----------------------------
-- Table structure for dt_apply
-- ----------------------------
DROP TABLE IF EXISTS `dt_apply`;
CREATE TABLE `dt_apply`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '申请表Id',
  `user_id` int(11) DEFAULT NULL COMMENT '申请人Id',
  `user_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '用户名',
  `verify_user_id` int(11) DEFAULT NULL COMMENT '审核人id',
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '标题',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '内容',
  `apply_type` int(4) DEFAULT NULL COMMENT '类型：0公益；1个人',
  `apply_state` int(4) DEFAULT NULL COMMENT '状态：0，审核中；1，通过；2拒绝',
  `goal` decimal(10, 2) DEFAULT NULL COMMENT '目标',
  `completed` int(4) DEFAULT NULL COMMENT '完成：0已经，1进行，2下线',
  `gmt_created` datetime(0) DEFAULT NULL COMMENT '创建时间',
  `gmt_modified` datetime(0) DEFAULT NULL COMMENT '修改时间',
  `file` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '照片链接',
  `is_deleted` int(4) DEFAULT NULL COMMENT '是否删除',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 29 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

-- ----------------------------
